import useForm from "../lib/useForm";

export const CreateProduct = () => {
  const { inputs, handleChange } = useForm({ name: "Nice", price: 23412 });
  return (
    <form>
      <label htmlFor="name">
        Name
        <input
          type="text"
          id="name"
          name="name"
          placeholder="Name"
          value={inputs.name}
          onChange={handleChange}
        />
      </label>
      <label htmlFor="price">
        Price
        <input
          type="number"
          id="price"
          name="price"
          placeholder="Name"
          value={inputs.price}
          onChange={handleChange}
        />
      </label>
    </form>
  );
};
