import { useMutation } from "@apollo/client";
import gql from "graphql-tag";
import useForm from "../lib/useForm";
import Form from "./styles/Form";

const CREATE_PRODUCT_MUTATION = gql`
  mutation CREATE_PRODUCT_MUTATION(
    $name: String!
    $description: String!
    $price: Int!
    $image: Upload
  ) {
    createProduct(
      data: {
        name: $name
        description: $description
        price: $price
        photo: {
          create : {
            image: $image, altText: $name
          }
        }
        status: 'AVAILABLE' 
      }
    ) {
      name
      description
      id
      price
    }
  }
`;

export const CreateProduct = () => {
  const { inputs, handleChange, clearForm, resetForm } = useForm({
    name: "Nice",
    price: 23412,
    image: "",
    description: ""
  });
  const [createProduct, { loading, error, data }] = useMutation(
    CREATE_PRODUCT_MUTATION,
    {
      variables: inputs
    }
  );
  return (
    <Form
      onSubmit={async e => {
        e.preventDefault();
        console.log(inputs);
        const res = await createProduct();
      }}
    >
      <fieldset>
        <label htmlFor="image">
          Image
          <input type="file" id="image" name="image" onChange={handleChange} />
        </label>
        <label htmlFor="name">
          Name
          <input
            type="text"
            id="name"
            name="name"
            placeholder="Name"
            value={inputs.name}
            onChange={handleChange}
          />
        </label>
        <label htmlFor="price">
          Price
          <input
            type="number"
            id="price"
            name="price"
            placeholder="Price"
            value={inputs.price}
            onChange={handleChange}
          />
        </label>
        <label htmlFor="description">
          Description
          <textarea
            type="textarea"
            id="description"
            name="description"
            placeholder="Description"
            value={inputs.description}
            onChange={handleChange}
          />
        </label>
        <button type="submit" onClick={clearForm}>
          + Add Product
        </button>
      </fieldset>
    </Form>
  );
};
