const DELETE_PRODUCT_MUTATION = gql`
  mutation DELETE_PRODUCT_MUTATION($id: ID!){
    deleteProduct($id) {
      id
      name
    }
  }
`;

export const DeleteProduct = ({ id, children }) => {
  return (
    <button
      type="button"
      onClick={() => {
        if (confirm("Are you sure you want to delete this item?")) {
        }
      }}
    >
      {children}
    </button>
  );
};
